import React, { Component } from "react";

class App extends Component {
  constructor() {
    super();
    this.state = {
      task: {
        text: ""
      },
      tasks: [],
    };
  }
  handleInputChange(e) {
    console.log(e.target.value);
    const text = e.target.value;
    this.state.task.text = text;
  }
  render() {
    const { task, tasks } = this.state;
    return (
      <div>
        <form>
          <label htmlFor="taskInput">Add your task</label>
          <input type="text" placeholder="Write something.." name="taskInput" onChange={this.handleInputChange}></input>
          <button type="submit">
            Submit Task
          </button>
        </form>
      </div>
    );
  }
}

export default App;
